//direction declaration
let direction = "lion" ;
//if condition
if(direction=="left"){
  console.log("red planet");
}
//else if condition : will be checked only if the first condition , which is "if" is false , therefore the else  will be true, in the other case the second statement else if will be false
else if(direction == "right"){
  console.log("purple planet");
}
else if(direction == "backwards"){
  console.log("back home");
}
else{
  console.log("air plane crashes");
}